import { Button, Input } from "antd";
import { useForm } from "react-hook-form";
import { Navigate } from 'react-router-dom';
import "./pages.scss";
const { TextArea } = Input;

function NewArticlePage() {

  const {register, formState: { errors }, handleSubmit} = useForm();

  const userAuthorized = localStorage.getItem("user");

  if(!userAuthorized){
    return <Navigate to='/sing-in' />
  }

  return (
    <div className="sing-page new-article">
      <p className="sing-page__header">Create new article</p>
      <span className="sing-page__name-input new-article__name-input">
        Title
      </span>
      <Input className="sing-page__input" placeholder="Title" />
      <span className="sing-page__name-input new-article__name-input">
        Short description
      </span>
      <Input className="sing-page__input" placeholder="Title" />

      <span className="sing-page__name-input new-article__name-input">
        Text
      </span>
      <TextArea
        // value={}
        // onChange={(e) => setValue(e.target.value)}
        placeholder="Text"
        autoSize={{ minRows: 8 }}
      />

      <span className="sing-page__name-input new-article__name-input">
        Tags
      </span>
      <div className="tags-options">
        <Input className="sing-page__input tag-input" placeholder="Tags" />
        <Button className="tags-options__button sing-page__button-delete">
          Delete
        </Button>
        <Button className="tags-options__button sing-page__button-add">
          Add tag
        </Button>
      </div>

      <Button className="send-tag-button sing-page__button">Send</Button>
      {/* <p className='sing-page__question'>Donâ€™t have an account? <Link to={'/sing_up'} className='sing-link' href="#">Sign Up</Link>.</p> */}
    </div>
  );
}

export default NewArticlePage;
