import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";


export const fetchRegUser = createAsyncThunk(
  "regUser/fetchRegUser",
  async (userData, { rejectWithValue }) => {
    try {
      const responseUser = await fetch(`https://blog.kata.academy/api/users`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify(userData),
      });

      if (!responseUser.ok) {
        throw new Error("responseUser error");
      }

      const user = await responseUser.json();
      localStorage.setItem("user", JSON.stringify(user));

      return user;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

export const fetchSignInUser = createAsyncThunk(
  "signInUser/fetchSignInUser",
  async (userData, { rejectWithValue }) => {
    try {
      const responseUser = await fetch(
        `https://blog.kata.academy/api/users/login`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json;charset=utf-8",
          },
          body: JSON.stringify(userData),
        }
      );

      if (!responseUser.ok) {
        throw new Error("responseUser error");
      }

      const user = await responseUser.json();
      localStorage.setItem("user", JSON.stringify(user));

      return user;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

const userSlice = createSlice({
  name: "userData",
  initialState: {
    userData: null,
    status: null,
    error: null,
  },
  reducers: {
    addUserData(state, action) {
      state.userData = action.payload;
    },
  },
  extraReducers: {
    [fetchRegUser.pending]: (state) => {
      state.status = "loading";
      state.error = null;
    },
    [fetchRegUser.fulfilled]: (state, actions) => {
      state.status = "resolved";
      state.userData = actions.payload;
      // console.log(actions.payload)
    },
    [fetchRegUser.rejected]: (state, actions) => {
      state.status = "rejected";
      state.error = actions.payload;
    },
    [fetchSignInUser.pending]: (state) => {
      state.status = "loading";
      state.error = null;
    },
    [fetchSignInUser.fulfilled]: (state, actions) => {
      state.status = "resolved";
      state.userData = actions.payload;
      // console.log(actions.payload)
    },
    [fetchSignInUser.rejected]: (state, actions) => {
      state.status = "rejected";
      state.error = actions.payload;
    },
  },
});

export const { addUserData } = userSlice.actions;
export default userSlice.reducer;
