import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";


export const fetchUser = createAsyncThunk(
  "regUser/fetchUser",
  async (userData, { rejectWithValue }) => {
    try {
      const responseUser = await fetch(`https://blog.kata.academy/api/users/login`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify(userData),
      });

      if (!responseUser.ok) {
        throw new Error("responseUser error");
      }

      const user = await responseUser.json();
      localStorage.setItem("user", JSON.stringify(user));

      return user;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY0M…wNDl9.XtielN4pTM6Nc2FEkCuPJFtW5BlWns1y4XXWJ9OlvYs';

export const fetchUserPut = createAsyncThunk(
  "regUser/fetchUserPut",
  async (userData, { rejectWithValue }) => {
    try {
      const responseUser = await fetch(`https://blog.kata.academy/api/user`, {
        method: "PUT",
        headers: {
          "Authorization": `Token ${token}`,
          "Content-Type": "application/json;charset=utf-8",
        },
        body: JSON.stringify(userData),
      });

      if (!responseUser.ok) {
        throw new Error("responseUser error");
      }

      const user = await responseUser.json();
      localStorage.setItem("user", JSON.stringify(user));

      return user;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

