import { Button, Input } from "antd";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { Navigate } from 'react-router-dom';
import "./pages.scss";

function NewArticlePage() {

  const [countTag, setCountTag] = useState(1);
  const [arrTag, setArrTag] = useState([]);

  const {register, formState: { errors }, handleSubmit} = useForm();

  const userAuthorized = localStorage.getItem("user");

  if(!userAuthorized){
    return <Navigate to='/sing-in' />
  }

  const onSubmitNewArcticle = (value) =>{
    console.log(value);
  }

  const onClickAddTag = () =>{
    setCountTag(countTag + 1);

    addTag(countTag);

  }

  // const onClickDeliteTag = () =>{

  // }

  // const deliteTag = () =>{
  //   setCountTag(countTag - 1);
  // }

  // const addTag = (counter) =>{
  //   const element = <><li><input {...register('tags')} className="sing-page__input tag-input" placeholder="Tags" /><Button className="tags-options__button sing-page__button-delete">
  //                     Delete
  //                   </Button></li></>;
  //   const array = [];
  //   for(let i = 0; i < counter; i++){
  //     array.push(element);
  //   }
  //   setArrTag([...arrTag, array]);
  //   return arrTag;
  // }

  const addTag = (counter) =>{
    // const element = <><li><input {...register('tags')} className="sing-page__input tag-input" placeholder="Tags" /><Button className="tags-options__button sing-page__button-delete">
    //                   Delete
    //                 </Button></li></>;
    const array = [];
    let id = 10;
    for(let i = 0; i < counter; i++){
      array.push(<><li key={id}><input {...register('tags')} className="sing-page__input tag-input" placeholder="Tags" /><Button className="tags-options__button sing-page__button-delete">
      Delete
    </Button></li></>);
    id++;
    }
    console.log(array)

    setArrTag(array);

  }



  return (
    <form onSubmit={handleSubmit(onSubmitNewArcticle)} className="sing-page new-article">
      <p className="sing-page__header">Create new article</p>
      <label className="sing-page__name-input new-article__name-input">
        Title
        <input {...register('title',
        {required: "Please enter a value.",

        })} className="sing-page__input" placeholder="Title" />
      </label>
      {errors.title ? <span style={{ color: "#F5222D" }}>
      {errors.title.message }
        </span> : null}

      <label className="sing-page__name-input new-article__name-input">
        Short description
        <input {...register('shortDescription', {

          required: "Please enter a value.",

        })} className="sing-page__input" placeholder="Title" />
      </label>
      {errors.shortDescription ? <span style={{ color: "#F5222D" }}>
      {errors.shortDescription.message }
        </span> : null}


      <label className="sing-page__name-input new-article__name-input">
        Text
        <textarea {...register ('text', {

          required: "Please enter a value.",

        })} className="new-article__name-textarea"
        placeholder="Text"
      />
      </label>
      {errors.text ? <span style={{ color: "#F5222D" }}>
      {errors.text.message }
        </span> : null}


      <label className="sing-page__name-input new-article__name-input">
        Tags

        <ul className="tags-options">
          {arrTag}
          {/* <li>

          <input {...register('tags')} className="sing-page__input tag-input" placeholder="Tags" />
        <Button className="tags-options__button sing-page__button-delete">
          Delete
        </Button>
        <Button onClick={onClickAddTag} className="tags-options__button sing-page__button-add">
          Add tag
        </Button>

          </li> */}

      </ul>
      </label>
      <Input type='submit' className="sing-page__button send-tag-button " value={'Send'}></Input>
    </form>
  );
}

export default NewArticlePage;
