import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";

import NewAccountPage from "../pages/new-account-page";
import SignInPage from "../pages/sign-in-page";
import NewArticlePage from "../pages/new-article-page";
import ArticlePage from "../pages/article-page";
import CardsList from "../cards-list";
import EditProfile from '../pages/edit-profile'
import { Pagination } from "antd";
import { Routes, Route, Link } from "react-router-dom";
import { fetchArticles } from "../store/articles-slice";
import { addUserData } from '../store/user-slice';

import "./app.scss";

function App() {
  const [pageNumber, setPageNumber] = useState(1);

  const articleData = useSelector((state) => state.articles.article);
  const userData = useSelector((state) => state.regUser.userData);

  console.log(userData)

  const articlesCount = useSelector(
    (state) => state.articles.articles.articlesCount
  );
  const pageCount = articlesCount ? Math.ceil(articlesCount / 5) : 1;

//   console.log(articleData.article);
    let slug = articleData?.article?.slug ? articleData.article.slug : null;

  const page = pageNumber * 5 - 5;

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchArticles(page));
  }, [pageNumber]);

  useEffect(()=>{

    const user = JSON.parse((localStorage.getItem('user')));

    if(localStorage.getItem('user')){
      dispatch(addUserData(user.user))
    }

  })








  const changePagination = (e) => {
    setPageNumber(e);
  };

  const cardListPage = (
    <div className="body-items">
      <CardsList />
      <Pagination
        onChange={changePagination}
        defaultCurrent={1}
        defaultPageSize={1}
        total={pageCount}
        className="body-items__pagination"
      />
    </div>
  );

  const logOut = () =>{
    localStorage.removeItem('user')
  }



  return (
    <div className="body">
      <header className="header">
        <Link to={"/"} className="blog-name">
          Realworld Blog
        </Link>
        <div className="header-user-information">
          <Link onClick={logOut} to={"/"} className="create-article link-log-out">Log Out</Link>

        <Link to={"/profile"} className="link-sing-up create-article">
            Edit profile
          </Link>

          <Link to={"/create_article"} className="link-sing-up create-article">
            Create article
          </Link>

          <Link to={"/sing_in"} className="sing-in-link">
            Sign In
          </Link>

          <Link to={"/sing_up"} className="link-sing-up">
            Sign Up
          </Link>
        </div>
      </header>
      <Routes>
        <Route path="/" element={cardListPage} />
        <Route path="/sing_up" element={<NewAccountPage />} />
        <Route path="/sing_in" element={<SignInPage />} />
        <Route path="/create_article" element={<NewArticlePage />} />
        <Route
          path={`/articles/${slug}`}
          element={<ArticlePage articleData={articleData.article} />}
        />
        <Route path="/profile" element={<EditProfile />} />
        <Route path="*" element={cardListPage} />
      </Routes>
    </div>
  );
}

export default App;
