import './card.scss';
import Rectangle from './Rectangle 1.svg';
import  {HeartOutlined, HeartFilled}  from '@ant-design/icons';
import {format} from 'date-fns';


function Card({userName, authorAvatar, createArticle, description, favoritesCount, title}) {

  console.log(createArticle, format(new Date(createArticle), 'MMMM, d, y'));
  format(new Date(createArticle), 'MMMM, d, y') 

  return(
    <div className='card'>
      <div className='card-header'>
      <p className='card-header__text'>{title}</p>
      <HeartOutlined className='card-header__button-like' />
      <HeartFilled className='card-header__button-like heart-filled' />

      <span className='card-header__likes-counter'>{favoritesCount}</span>

      </div>

      <button>Tag1</button>
      <p className='text-card'>{description}</p>
      <div className='user-information'>
        <div className='user-information__card'>
        <span className='user-information__name' >{userName}</span>
        <span className='user-information__publication-date'>March 5, 2020 </span>
        </div>

        <img src={authorAvatar} alt='User avatar' className='user-information__avatar' />
      </div>
    </div>
  )
}

export default Card;
