import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';

import NewAccountPage from '../pages/new-account-page';
import SignInPage from '../pages/sign-in-page';
import NewArticlePage from '../pages/new-article-page'
import CardsList from '../cards-list';
import { Pagination } from 'antd';
import { Routes, Route, Link} from 'react-router-dom';
import {fetchArticles} from '../store/articles-slice'

import './app.scss';


function App() {

    const [pageNumber, setPageNumber] = useState(1)

    const articlesCount = useSelector((state=> state.articles.articles.articlesCount));
    const pageCount = articlesCount ? Math.ceil(articlesCount/ 5) : 1;

    // console.log(pageCount);


    const page = (pageNumber * 5) - 5;

    const dispatch = useDispatch();

    useEffect(()=>{
       dispatch(fetchArticles(page));
    }, [pageNumber])

   const changePagination = (e) =>{
        setPageNumber(e);
    }

    const cardListPage = <div className='body-items'>
    <CardsList />
    <Pagination onChange={changePagination} defaultCurrent={1} defaultPageSize={1} total={40} className='body-items__pagination'/>
</div>

    return(
        <div className='body'>
            <header className='header'>
                <Link to={'/'} className='blog-name'>Realworld Blog</Link>
                <div className='header-user-information'>
                    <Link to={'/Create_article'} className='link-sing-up create-article'>Create article</Link>
                    <Link to={'/sing_in'} className='sing-in-link'>Sign In</Link>
                    <Link to={'/sing_up'} className='link-sing-up'>Sign Up</Link>

                </div>

            </header>
            <Routes>
                <Route path='/' element={cardListPage} />
                <Route path='/sing_up' element={<NewAccountPage />} />
                <Route path='/sing_in' element={<SignInPage />}/>
                <Route path='/Create_article' element={<NewArticlePage />}/>
            </Routes>
        </div>
    )
}

export default App;
