import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

export const fetchArticles = createAsyncThunk('articles/fetchArticles', async (_, { rejectWithValue}) => {
  try {
    const responseArticles = await fetch('https://api.realworld.io/api/articles?limit=5&offset=5');

    if (!responseArticles.ok) {
      throw new Error('responseId error');
    }

    const articles = await responseArticles.json();

    return articles

    }catch (error) {
    return rejectWithValue(error.message);
  }
});

const articlesSlice = createSlice({
  name: 'articles',
  initialState: {
    articles: [],
    status: null,
    error: null,
  },
  // reducers: {
  //   updateTickets(state, actions) {
  //     state.tickets = actions.payload;
  //   },
  //   updateDataTickets(state, actions) {
  //     state.ticketsData = actions.payload;
  //   },
  // },
  extraReducers: {
    [fetchArticles.pending]: (state) => {
      state.status = 'loading';
      state.error = null;
    },
    [fetchArticles.fulfilled]: (state, actions) => {
      state.status = 'resolved';
      state.articles = actions.payload;
    },
    [fetchArticles.rejected]: (state, actions) => {
      state.status = 'rejected';
      state.error = actions.payload;
    },
  },
});

// export const { updateTickets, updateDataTickets } = ticketsSlice.actions;
export default articlesSlice.reducer;
