import { Checkbox, Input, message } from "antd";
import {useForm, Controller} from 'react-hook-form';
import { Link } from "react-router-dom";
import "./pages.scss";

function NewAccountPage() {

  const {register, getValues,
          formState: {
            errors,
          },
        handleSubmit,
        control,
      } = useForm();

  const onSubmitForm = (data) =>{
    console.log(data);
  }

  const onChange = (e) =>{
    console.log(e.value)
  }

  return (
    <form onSubmit={handleSubmit(onSubmitForm)} className="sing-page">
      <p className="sing-page__header">Create new account</p>

      <label className="sing-page__name-input">
        Username
          <input {...register('username',
           {required : 'Please enter a value.',
            minLength:{
              value: 3,
              message: 'Your user name needs to be at least 3 characters.'
            }, 
            maxLength:{
              value: 20,
              message: 'Your user name must be no more than 20 characters.'
            },
           })} 
           className="sing-page__input" placeholder="Username" />
      </label>
        {errors?.username && <span style={{color: '#F5222D'}}>{errors?.username?.message || 'Error!'}</span>}


      <label className="sing-page__name-input">
        Email address
          <input {...register('email', {
            required : 'Please enter email.',
            pattern: {
              value: /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/iu,
              message: 'Please enter a valid email.',
            },
            })} className="sing-page__input" placeholder="Email address" />
      </label>
      {errors?.email && <span style={{color: '#F5222D'}}>{errors?.email?.message || 'Error!'}</span>}

      <label className="sing-page__name-input">
        Password
          <input {...register('password', {required : 'Please enter a value.',
            minLength: {
              value: 6,
              message: 'Your password needs to be at least 6 characters.'
            },
            maxLength: {
              value: 40,
              message: 'Your user name must be no more than 20 characters.'
            },
           })}
          className="sing-page__input" placeholder="Password" />
      </label>
      {errors?.password && <span style={{color: '#F5222D'}}>{errors?.password?.message || 'Error!'}</span>}

      <label className="sing-page__name-input">
        Repeat Password
        <input {...register('repeatPassword',
        {required : 'Please enter password.',
          validate: value => value === getValues('password'),
        })} 
        className="sing-page__input" placeholder="Password" />
      </label>
      {errors?.repeatPassword && <span style={{color: '#F5222D'}}>{errors?.repeatPassword?.message || 'Passwords must match!'}</span>}

        {/* <input type='checkbox' className="checkbox-sing-up" 
        checked={false/true} {...register("checkbox")}></input> */}

<Controller
   name="checkbox"
  //  defaultValue={false}
   control={control}
   render={({ onChange, value }) => (
    <Checkbox onChange={onChange} value={value}  className="checkbox-sing-up">
    I agree to the processing of my personal information
  </Checkbox>
   )}/>    

      {/* <Checkbox  className="checkbox-sing-up">
        I agree to the processing of my personal information
      </Checkbox> */}

      <Input type="submit" className="sing-page__button" value={'Create'}></Input>
      <p className="sing-page__question">
        Already have an account?{" "}
        <Link className="sing-link" to={"/sing_in"}>
          Sign In
        </Link>
        .
      </p>
    </form>
  );
}

export default NewAccountPage;
