import { Input } from "antd";
import { Link } from "react-router-dom";
import { useForm } from 'react-hook-form';
import "./pages.scss";

function SignInPage() {

  const {register,
    formState: {
      errors,
    },
  handleSubmit,
} = useForm();

const onSubmitSignIn = (value) =>{

  const data = {
    user: {
      email: value.email,
      password: value.password,
    }
  }

  console.log(data);
}

  return (
    <form onSubmit={handleSubmit(onSubmitSignIn)} className="sing-page">
      <p className="sing-page__header">Sign In</p>
      <label className="sing-page__name-input">
        Email address
        <input {...register('email',{
          required : 'Please enter email.',
          pattern: {
            value: /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/iu,
            message: 'Please enter a valid email.',
          },
        })} className="sing-page__input" placeholder="Email address" />
        </label>
        {errors?.email && <span style={{color: '#F5222D'}}>{errors?.email?.message || 'Error!'}</span>}


      <label className="sing-page__name-input">
        Password
        <input {...register('password', {
          required: 'Please enter password.',
          minLength: {
            value: 6,
            message: 'Your password needs to be at least 6 characters.'
          },
          maxLength: {
            value: 40,
            message: 'Your user name must be no more than 20 characters.'
          },
        })} className="sing-page__input" placeholder="Password" />
        </label>
        {errors?.password && <span style={{color: '#F5222D'}}>{errors?.password?.message || 'Error!'}</span>}

      <Input type="submit" className="sing-page__button" value={'Login'}></Input>
      <p className="sing-page__question">
        Donâ€™t have an account?{" "}
        <Link to={"/sing_up"} className="sing-link">
          Sign Up
        </Link>
        .
      </p>
    </form>
  );
}

export default SignInPage;
