import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";
import { Link, Outlet } from 'react-router-dom';
import { addUserData } from "../store/user-slice";
import avatar from "./avatar.svg";

const Layout = () =>{

    const userData = useSelector((state) => state.regUser.userData);

    const user = JSON.parse(localStorage.getItem("user"));

    const dispatch = useDispatch();

    const personDitails = userData ? (
        <Link to={"/profile"} className="user-info">
          <span className="user-info__name">{userData.username}</span>
          <img src={avatar} alt="User avatar" className="user-info__avatar" />
        </Link>
      ) : null;

      const logOut = () => {
        localStorage.removeItem("user");
      };

      useEffect(() => {


        if (localStorage.getItem("user")) {
          dispatch(addUserData(user.user));
        }
      }, []);


    return(
        <>
        <header className="header">
        <Link to={"/"} className="blog-name">
          Realworld Blog
        </Link>
        <div className="header-user-information">
          <Link to={"/create_article"} className="link-sing-up create-article">
            Create article
          </Link>

          {personDitails}

          {userData ? (
            <Link
              onClick={logOut}
              to={"/"}
              className="create-article link-log-out"
            >
              Log Out
            </Link>
          ) : null}

          {!userData ? (
            <Link to={"/sing_in"} className="sing-in-link">
              Sign In
            </Link>
          ) : null}

          {!userData ? (
            <Link to={"/sing_up"} className="link-sing-up">
              Sign Up
            </Link>
          ) : null}
        </div>
      </header>
      <Outlet />
      </>
    )
}

export default Layout;
