import './cards-list.scss';
import Card from '../card';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import {format} from 'date-fns';


function CardsList() {

  const [id, setId] = useState(10);



const articlesData = useSelector(state => state.articles.articles);
// console.log(articlesData.articles)

const articles = articlesData.length !== 0 ? articlesData.articles.map((el, idx)=>{

  const userName = el.author.username;
  const authorAvatar = el.author.image;
  const createArticle = format(new Date(el.createdAt), 'MMMM d, y');
  const description = el.description;
  const favoritesCount = el.favoritesCount;
  const title = el.title;
  const tagList = el.tagList

  return(
    <li key={id + idx} className='cards-list__item'>
        <Card el={el} userName={userName} authorAvatar={authorAvatar} createArticle={createArticle} id={id + idx}
          description={description} favoritesCount={favoritesCount} title={title} tagList={tagList}/>
      </li>
  )

}) : null;


  return(
    <ul className='cards-list'>

      {articles}

    </ul>
  )
}

export default CardsList;
