import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const fetchArticles = createAsyncThunk(
  "articles/fetchArticles",
  async (value, { rejectWithValue }) => {
    try {
      const responseArticles = await fetch(
        `https://api.realworld.io/api/articles?limit=5&offset=${value}`
      );
      // ('https://api.realworld.io/api/articles?limit=5&offset=5');
      if (!responseArticles.ok) {
        throw new Error("responseId error");
      }

      const articles = await responseArticles.json();

      return articles;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

export const fetchArticle = createAsyncThunk(
  "article/fetchArticle",
  async (value, { rejectWithValue, dispatch }) => {
    try {
      const responseArticle = await fetch(
        `https://api.realworld.io/api/articles/${value}`
      );

      if (!responseArticle.ok) {
        throw new Error("responseId error");
      }

      const article = await responseArticle.json();

      return article;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

// https://api.realworld.io/api/articles/If-we-quantify-the-alarm-we-can-get-to-the-FTP-pixel-through-the-online-SSL-interface!-120863

const articlesSlice = createSlice({
  name: "articl",
  initialState: {
    articles: [],
    article: [],
    defaultPath: "/",
    status: null,
    error: null,
  },
  reducers: {
    async selectArticle(state, actions) {
      const fetchArticle = await fetch(`https://api.realworld.io/api/articles/${actions.payload}`);
      const acticleData = await fetchArticle.json();
      console.log(acticleData.article);
      state.article = await acticleData.article;
    },
  },
  extraReducers: {
    [fetchArticles.pending]: (state) => {
      state.status = "loading";
      state.error = null;
    },
    [fetchArticles.fulfilled]: (state, actions) => {
      state.status = "resolved";
      state.articles = actions.payload;
    },
    [fetchArticles.rejected]: (state, actions) => {
      state.status = "rejected";
      state.error = actions.payload;
    },
  },
});

export const { selectArticle } = articlesSlice.actions;
export default articlesSlice.reducer;
